import React, { createContext, useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AuthContext = createContext();

export const useAuth = () => {
  return useContext(AuthContext);
};

export const AuthProvider = ({ children }) => {
  const [currentUser, setCurrentUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  // Check if user is already logged in
  useEffect(() => {
    const token = localStorage.getItem('token');

    if (token) {
      // Set default axios auth header
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;

      // Get user data
      fetchUserData();
    } else {
      setLoading(false);
    }
  }, []);

  const fetchUserData = async () => {
    try {
      const response = await axios.get('/api/users/me');
      setCurrentUser(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching user data', error);
      localStorage.removeItem('token');
      delete axios.defaults.headers.common['Authorization'];
      setCurrentUser(null);
      setLoading(false);
    }
  };

  const login = async (email, password) => {
    try {
      const response = await axios.post('/api/auth/token', new URLSearchParams({
        'username': email,
        'password': password
      }), {
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded'
        }
      });

      const { access_token } = response.data;

      // Store token in localStorage
      localStorage.setItem('token', access_token);

      // Set default axios auth header
      axios.defaults.headers.common['Authorization'] = `Bearer ${access_token}`;

      // Get user data
      await fetchUserData();

      return true;
    } catch (error) {
      console.error('Login error', error);
      setError(error.response?.data?.detail || 'Login failed');
      return false;
    }
  };

  const register = async (email, password, username) => {
    try {
      await axios.post('/api/auth/register', {
        email,
        password,
        username
      });

      // Login after registration
      return await login(email, password);
    } catch (error) {
      console.error('Registration error', error);
      setError(error.response?.data?.detail || 'Registration failed');
      return false;
    }
  };

  const logout = () => {
    // Remove token
    localStorage.removeItem('token');

    // Remove auth header
    delete axios.defaults.headers.common['Authorization'];

    // Clear user
    setCurrentUser(null);

    // Redirect to login
    navigate('/login');
  };

  const updateMembership = async (membershipType) => {
    try {
      const response = await axios.put(`/api/users/membership?membership_type=${membershipType}`);
      setCurrentUser(response.data);
      return true;
    } catch (error) {
      console.error('Error updating membership', error);
      setError(error.response?.data?.detail || 'Failed to update membership');
      return false;
    }
  };

  const value = {
    currentUser,
    loading,
    error,
    login,
    register,
    logout,
    updateMembership
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};
